import React, { useState } from 'react';

function BookingLookupForm({ onLookup }) {
  const [code, setCode] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onLookup(code);
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded shadow-sm bg-light">
      <label className="form-label">Enter your confirmation code:</label>
      <input type="text" className="form-control mb-2" value={code} onChange={(e) => setCode(e.target.value)} required />
      <button type="submit" className="btn btn-primary">Find Booking</button>
    </form>
  );
}

export default BookingLookupForm;
