import React, { useState } from 'react';

// Mock trips for the dropdown (replace later with API)
const mockTrips = [
  { trip_id: 1, trip_name: 'European Adventure', duration: 10 },
  { trip_id: 2, trip_name: 'Tropical Escape', duration: 7 }
];

function BookingForm({ onSubmit }) {
  const [tripId, setTripId] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ tripId, startDate, endDate });
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded shadow-sm bg-light">
      <div className="mb-3">
        <label className="form-label">Select Trip Package</label>
        <select
          className="form-select"
          value={tripId}
          onChange={(e) => setTripId(e.target.value)}
          required
        >
          <option value="">-- Choose a trip --</option>
          {mockTrips.map((trip) => (
            <option key={trip.trip_id} value={trip.trip_id}>
              {trip.trip_name} ({trip.duration} days)
            </option>
          ))}
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label">Start Date</label>
        <input
          type="date"
          className="form-control"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">End Date</label>
        <input
          type="date"
          className="form-control"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          required
        />
      </div>

      <div className="form-check mb-3">
      <input
        className="form-check-input"
        type="checkbox"
        checked={true}
        disabled
        id="packageIncluded"
        />
        <label className="form-check-label text-muted" htmlFor="packageIncluded">
          Package includes default hotel and flight arrangements
        </label>
      </div>
      <button type="submit" className="btn btn-primary">Confirm Booking</button>
    </form>
  );
}

export default BookingForm;
