import React, { useState } from 'react';
import axios from 'axios';

function BookFlightPage() {
  const [confirmationCode, setConfirmationCode] = useState('');
  const [booking, setBooking] = useState(null);
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [message, setMessage] = useState('');

  const handleLoad = async () => {
    setMessage('');
    try {
      const { data } = await axios.get(`/api/bookings/by-code/${confirmationCode}`);
      setBooking(data.booking);
      // Load available flights for this trip
      const flightRes = await axios.get(`/api/flights/by-trip/${data.booking.trip_id}`);
      setFlights(flightRes.data);
    } catch (err) {
      setBooking(null);
      setFlights([]);
      setMessage('Booking not found or error loading flights.');
    }
  };

  const handleBookFlight = async () => {
    if (!selectedFlight) return;
    try {
      await axios.post('/api/booking-flight', {
        bookingId: booking.booking_id,
        flightId: selectedFlight
      });
      setMessage('✅ Flight booked successfully!');
    } catch (err) {
      setMessage('❌ Failed to book flight: ' + err.response?.data?.error || 'Unknown error');
    }
  };

  return (
    <div className="container mt-4">
      <h3>Book a Flight</h3>

      <div className="mb-3">
        <label>Enter your confirmation code:</label>
        <input className="form-control"
          value={confirmationCode}
          onChange={(e) => setConfirmationCode(e.target.value)}
        />
        <button className="btn btn-primary mt-2" onClick={handleLoad}>Load Booking</button>
      </div>

      {booking && (
        <>
          <div className="alert alert-info">
            Booking for <strong>{booking.trip_name}</strong> ({booking.start_date} to {booking.end_date})
          </div>

          <h5>Available Flights</h5>
          {flights.length === 0 ? <p>No flights found for this trip.</p> : (
            <ul className="list-group">
              {flights.map(f => (
                <li key={f.flight_id} className={`list-group-item ${selectedFlight === f.flight_id ? 'active' : ''}`}
                  onClick={() => setSelectedFlight(f.flight_id)} style={{ cursor: 'pointer' }}>
                  ✈ {f.airline_name} | {f.departure_airport} → {f.arrival_airport} | ${f.price}
                </li>
              ))}
            </ul>
          )}
          <button className="btn btn-success mt-3" disabled={!selectedFlight} onClick={handleBookFlight}>
            Confirm Flight Selection
          </button>
        </>
      )}

      {message && <div className="alert alert-info mt-3">{message}</div>}
    </div>
  );
}

export default BookFlightPage;
