import React, { useState } from 'react';

function RateTripPage() {
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Rate Your Trip</h2>
      {submitted ? (
        <p>âœ… Thank you for your feedback!</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <label>Confirmation Code:</label><br />
          <input type="text" required /><br />
          <label>Rating (1-5):</label><br />
          <input type="number" min="1" max="5" required /><br />
          <label>Feedback:</label><br />
          <textarea required></textarea><br /><br />
          <button type="submit">Submit Rating</button>
        </form>
      )}
    </div>
  );
}

export default RateTripPage;
