import React, { useState } from 'react';

// Mock trips for the dropdown (replace later with API)
const mockTrips = [
  { trip_id: 1, trip_name: 'European Adventure', duration: 10 },
  { trip_id: 2, trip_name: 'Tropical Escape', duration: 7 }
];

function BookingForm({ onSubmit }) {
  const [tripId, setTripId] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ tripId, startDate, endDate });
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded shadow-sm bg-light">
      {tripId && (
        <div className="mb-3">
          <label className="form-label">Trip Selected</label>
          <input type="text" className="form-control" value={`Trip ID: ${tripId}`} readOnly />
        </div>
      )}

      <div className="mb-3">
        <label className="form-label">Start Date</label>
        <input
          type="date"
          className="form-control"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">End Date</label>
        <input
          type="date"
          className="form-control"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          required
        />
      </div>

      <div className="form-check mb-3">
      <input
        className="form-check-input"
        type="checkbox"
        checked={true}
        disabled
        id="packageIncluded"
        />
        <label className="form-check-label text-muted" htmlFor="packageIncluded">
          We only offer package deals that includes hotel and flight.
        </label>
      </div>
      <button type="submit" className="btn btn-primary">Confirm Booking</button>
    </form>
  );
}

export default BookingForm;
