import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import TripCard from '../components/TripCard';
import {
  fetchTrips,
  fetchTopRatedTrips,
  fetchTopBookedTrips,
} from '../api/tripApi';

function HomePage() {
  const [trips, setTrips] = useState([]);
  const [allTrips, setAllTrips] = useState([]);
  const [showTopRated, setShowTopRated] = useState(false);
  const [showTopBooked, setShowTopBooked] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    async function loadTrips() {
      const data = await fetchTrips();
      setTrips(data);
      setAllTrips(data);
    }
    loadTrips();
  }, []);

  const handleViewDetails = (tripId) => {
    navigate(`/trip/${tripId}`);
  };

  const handleToggleTopRated = async () => {
    if (showTopRated) {
      setTrips(allTrips);
    } else {
      const topTrips = await fetchTopRatedTrips();
      setTrips(topTrips);
    }
    setShowTopRated(!showTopRated);
    setShowTopBooked(false); // turn off the other toggle
  };

  const handleToggleTopBooked = async () => {
    if (showTopBooked) {
      setTrips(allTrips);
    } else {
      const topBooked = await fetchTopBookedTrips();
      setTrips(topBooked);
    }
    setShowTopBooked(!showTopBooked);
    setShowTopRated(false); // turn off the other toggle
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Available Trips</h2>

      <div className="d-flex gap-3 mb-3">
        <button
          className="btn btn-outline-info"
          onClick={handleToggleTopRated}
        >
          {showTopRated ? 'Show All Trips' : 'Show Top 5 Rated Trips'}
        </button>

        <button
          className="btn btn-outline-warning"
          onClick={handleToggleTopBooked}
        >
          {showTopBooked ? 'Show All Trips' : 'Show Top 5 Booked Trips'}
        </button>
      </div>

      <div className="row">
        {trips.map((trip) => (
          <div key={trip.trip_id} className="col-md-4 mb-4">
            <TripCard trip={trip} onView={handleViewDetails} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
