import React from 'react';
import { useNavigate } from 'react-router-dom';
import TripCard from '../components/TripCard';

// Mock trip data
const mockTrips = [
  {
    trip_id: 1,
    trip_name: 'European Adventure',
    trip_type: 'Cultural',
    duration: 10,
    base_price: 1200.00,
    description: 'Explore iconic cities across Europe.'
  },
  {
    trip_id: 2,
    trip_name: 'Tropical Escape',
    trip_type: 'Relaxation',
    duration: 7,
    base_price: 950.00,
    description: 'Relax on sunny beaches with clear blue waters.'
  }
];

function HomePage() {
  const navigate = useNavigate();

  const handleViewDetails = (tripId) => {
    navigate(`/trip/${tripId}`);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Available Trips</h2>
      <div className="row">
        {mockTrips.map((trip) => (
          <div key={trip.trip_id} className="col-md-4 mb-4">
            <TripCard trip={trip} onView={handleViewDetails} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
