import React, { useState } from 'react';
import axios from 'axios';

function BookHotelPage() {
  const [confirmationCode, setConfirmationCode] = useState('');
  const [booking, setBooking] = useState(null);
  const [hotels, setHotels] = useState([]);
  const [selectedHotel, setSelectedHotel] = useState(null);
  const [message, setMessage] = useState('');

  const handleLoad = async () => {
    setMessage('');
    try {
      const { data } = await axios.get(`/api/bookings/by-code/${confirmationCode}`);
      setBooking(data.booking);
      const res = await axios.get(`/api/hotels/by-trip/${data.booking.trip_id}`);
      setHotels(res.data);
    } catch (err) {
      setBooking(null);
      setHotels([]);
      setMessage('Booking not found or error loading hotels.');
    }
  };

  const handleBookHotel = async () => {
    if (!selectedHotel) return;
    try {
      await axios.post('/api/booking-hotel', {
        bookingId: booking.booking_id,
        hotelId: selectedHotel
      });
      setMessage('‚úÖ Hotel booked successfully!');
    } catch (err) {
      setMessage('‚ùå Failed to book hotel: ' + err.response?.data?.error || 'Unknown error');
    }
  };

  return (
    <div className="container mt-4">
      <h3>Book a Hotel</h3>

      <div className="mb-3">
        <label>Enter your confirmation code:</label>
        <input className="form-control"
          value={confirmationCode}
          onChange={(e) => setConfirmationCode(e.target.value)}
        />
        <button className="btn btn-primary mt-2" onClick={handleLoad}>Load Booking</button>
      </div>

      {booking && (
        <>
          <div className="alert alert-info">
            Booking for <strong>{booking.trip_name}</strong> ({booking.start_date} to {booking.end_date})
          </div>

          <h5>Available Hotels</h5>
          {hotels.length === 0 ? <p>No hotels found for this trip.</p> : (
            <ul className="list-group">
              {hotels.map(h => (
                <li key={h.hotel_id} className={`list-group-item ${selectedHotel === h.hotel_id ? 'active' : ''}`}
                  onClick={() => setSelectedHotel(h.hotel_id)} style={{ cursor: 'pointer' }}>
                  üè® {h.name} ({h.city}, {h.country}) | ‚≠ê {h.star_rating}
                </li>
              ))}
            </ul>
          )}
          <button className="btn btn-success mt-3" disabled={!selectedHotel} onClick={handleBookHotel}>
            Confirm Hotel Selection
          </button>
        </>
      )}

      {message && <div className="alert alert-info mt-3">{message}</div>}
    </div>
  );
}

export default BookHotelPage;
