import React, { useState } from 'react';
import {
  fetchBookingByConfirmation,
  cancelBookingByCode
} from '../api/tripApi';

function BookingLookupCard() {
  const [code, setCode] = useState('');
  const [booking, setBooking] = useState(null);
  const [error, setError] = useState('');
  const [cancelSuccess, setCancelSuccess] = useState('');

  const handleLookup = async () => {
    setError('');
    setBooking(null);
    setCancelSuccess('');

    const result = await fetchBookingByConfirmation(code.trim());

    if (result.success) {
      setBooking(result.booking);
    } else {
      setError(result.error || 'Booking not found');
    }
  };

  const handleCancel = async () => {
    const confirm = window.confirm('Are you sure you want to cancel this booking?');
    if (!confirm) return;

    const result = await cancelBookingByCode(code.trim());

    if (result.success) {
      setCancelSuccess('Booking cancelled successfully.');
      setBooking({ ...booking, booking_status: 'Cancelled' });
    } else {
      setError(result.error || 'Failed to cancel booking.');
    }
  };

  return (
    <div className="container mt-4">
      <h4>View Your Booking</h4>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter Confirmation Code"
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <button className="btn btn-outline-primary" onClick={handleLookup}>
          Look Up
        </button>
      </div>

      {error && <div className="alert alert-danger">{error}</div>}
      {cancelSuccess && <div className="alert alert-success">{cancelSuccess}</div>}

      {booking && (
        <div className="card p-3 shadow-sm">
          <h5 className="mb-2 text-success">{booking.trip_name}</h5>
          <p><strong>Type:</strong> {booking.trip_type}</p>
          <p><strong>Dates:</strong> {booking.start_date} to {booking.end_date}</p>
          <p><strong>Status:</strong> {booking.booking_status}</p>
          <p><strong>Total Price:</strong> ${booking.total_price}</p>
          <button
            className="btn btn-danger mt-2"
            disabled={booking.booking_status === 'Cancelled'}
            onClick={handleCancel}
          >
            Cancel Booking
          </button>
        </div>
      )}
    </div>
  );
}

export default BookingLookupCard;
