import React from 'react';
import { useParams } from 'react-router-dom';

const mockTrip = {
  trip_id: 1,
  trip_name: 'European Adventure',
  description: 'Explore 5 countries in 10 days.',
  trip_type: 'Cultural',
  duration: 10,
  base_price: 1200,
  ratings: [
    { rating_score: 5, feedback: 'Amazing!', customer_name: 'Alice' },
    { rating_score: 4, feedback: 'Very good experience.', customer_name: 'Bob' }
  ]
};

function TripDetailsPage() {
  const { id } = useParams();

  return (
    <div style={{ padding: '20px' }}>
      <h2>{mockTrip.trip_name}</h2>
      <p><strong>Description:</strong> {mockTrip.description}</p>
      <p><strong>Type:</strong> {mockTrip.trip_type}</p>
      <p><strong>Duration:</strong> {mockTrip.duration} days</p>
      <p><strong>Price:</strong> ${mockTrip.base_price}</p>
      <h3>Customer Reviews</h3>
      <ul>
        {mockTrip.ratings.map((r, i) => (
          <li key={i}>
            ⭐ {r.rating_score} — "{r.feedback}" — {r.customer_name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TripDetailsPage;
