import React from 'react';

function TripCard({ trip, onView }) {
  return (
    <div className="card h-100 shadow-sm">
      <div className="card-body">
        <h5 className="card-title"
        style={{ color: 'rgb(47, 166, 120)', fontWeight: 'bold' }}>{trip.trip_name}</h5>
        <p><strong>Type:</strong> {trip.trip_type}</p>
        <p><strong>Duration:</strong> {trip.duration} days</p>
        <p><strong>Price:</strong> ${trip.base_price}</p>
        <p><strong>Rating:</strong> {trip.avg_rating ?? 'No ratings yet'}</p>
        <p>{trip.description}</p>
        <button className="btn btn-success" onClick={() => onBook(trip.trip_id)}>
          Book This Trip
        </button>
      </div>
    </div>
  );
}

export default TripCard;
