import React, { useState } from 'react';

// Mock trip options (replace with API data later)
const tripOptions = [
  { trip_id: 1, trip_name: 'European Adventure', duration: 10 },
  { trip_id: 2, trip_name: 'Tropical Escape', duration: 7 }
];

function BookingForm({ onSubmit }) {
  const [selectedTripId, setSelectedTripId] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      tripId: selectedTripId,
      startDate,
      endDate
    });
  };

  return (
    <form onSubmit={handleSubmit} className="p-3 border rounded shadow-sm bg-light">
      {/* Trip Dropdown */}
      <div className="mb-3">
        <label className="form-label">Select a Trip</label>
        <select
          className="form-select"
          value={selectedTripId}
          onChange={(e) => setSelectedTripId(e.target.value)}
          required
        >
          <option value="">-- Choose a trip --</option>
          {tripOptions.map((trip) => (
            <option key={trip.trip_id} value={trip.trip_id}>
              {trip.trip_name} ({trip.duration} days)
            </option>
          ))}
        </select>
      </div>

      {/* Start and End Dates */}
      <div className="mb-3">
        <label className="form-label">Start Date</label>
        <input
          type="date"
          className="form-control"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
          required
        />
      </div>

      <div className="mb-3">
        <label className="form-label">End Date</label>
        <input
          type="date"
          className="form-control"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
          required
        />
      </div>

      <button type="submit" className="btn btn-success">Book Trip</button>
    </form>
  );
}

export default BookingForm;
